import { Component, Inject, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import {MatDialog, MatDialogRef, MAT_DIALOG_DATA} from '@angular/material/dialog';
import { Router } from '@angular/router';
import  * as $ from "jquery";
import { AngularFireStorage } from '@angular/fire/storage';
import { NotificationService } from '../../../services/notification.service';
import { SubscriptionService } from '../../../services/ModlesServices/subscription.service';
import { MemberService } from '../../../services/ModlesServices/member.service';
import { Member } from '../../../models/Member';
import { Subscription } from 'src/app/components/models/Subscription';
import  {jsPDF} from 'jspdf';

@Component({
  selector: 'app-activate',
  templateUrl: './activate.component.html',
  styleUrls: ['./activate.component.scss']
})
export class ActivateComponent implements OnInit {
  memberr=new Member()
  sub_Starter=new Subscription();
  sub_Standard=new Subscription();
  sub_Deluxe=new Subscription();
  constructor(private router: Router,
     private angf:AngularFireStorage,
     private subService:SubscriptionService,
     private memberService:MemberService,

    private notification:NotificationService,
    public dialogRef: MatDialogRef<ActivateComponent> ,
   @Inject(MAT_DIALOG_DATA) public me: Member ) {
    this.memberr=me
  }
  ngOnInit() {
    this.setvalues()
    this.getSubs()

   } 
   setvalues(){
    $("#name").val( this.memberr.firstname+" "+this.memberr.lastname);
 }
 getSubs(){
  this.subService.getSubByName("Starter").subscribe(data=>{this.sub_Starter=data
  this.sub_Starter.duration_month=this.sub_Starter.duration/30;
  })
  this.subService.getSubByName("Standard").subscribe(data=>{this.sub_Standard=data
    this.sub_Standard.duration_month=this.sub_Standard.duration/30;})

  this.subService.getSubByName("Deluxe").subscribe(data=>{this.sub_Deluxe=data
    this.sub_Deluxe.duration_month=this.sub_Deluxe.duration/30;})
}
   close() {
    this.dialogRef.close();
  }

  upSub(UpSub:NgForm){
    this.memberr=UpSub.value

    if(UpSub.invalid){      this.notification.showNotification('top','center','Please fill all the required fields','danger');    }
    else{ 
      if(this.memberr.subscriptionName=="Cutomized"){this.setCustomSub()}
      else{    this.setSub()}
      setTimeout(() => { 
       this.me.subscriptionId=this.memberr.subscriptionId
       this.me.subscriptionDate=this.memberr.subscriptionDate
       this.me.subscriptionExpire=this.memberr.subscriptionExpire
       this.me.subscriptionName=this.memberr.subscriptionName
       this.me.PayMethod=this.memberr.PayMethod
       this.me.priceSUB=this.memberr.priceSUB


     this.memberService.UpdateMember(this.me).subscribe(
        response=>{
          if(response){ this.notification.showNotification('top','center','Your Subscription Has been Updated Successfuly!','success');
       this.getreceipt(this.me);

        window.location.reload();
        }
        if(!response){ this.notification.showNotification('top','center','Something went wrong!','daner');
        }
        }
      
        
      )

      },350)

    }

  }

  utc = new Date()

  setCustomSub(){
    if(this.memberr.subscriptionDuration<7||this.memberr.subscriptionDuration>30){
      this.notification.showNotification('top','center','Cutomized pack s duration is : MIN:7 and MAX:30 ','danger');


    }
     else{ 
       this.utc.setHours(0,0,0,0)

    this.memberr.subscriptionId=null;

    this.memberr.subscriptionDate=this.utc;

   this.memberr.subscriptionExpire=this.addDays(this.memberr.subscriptionDuration);
   this.memberr.subscriptionExpire.setHours(0,0,0,0);} 
   this.memberr.priceSUB=this.memberr.subscriptionDuration*50;

  }

  setSub(){
   
    
    this.utc.setHours(0,0,0,0)

 this.memberr.subscriptionId=parseInt(this.memberr.subscriptionName);

 this.memberr.subscriptionDate=this.utc;

 this.subService.getSubByName(this.memberr.subscriptionName).subscribe(data=>
   {       this.memberr.subscriptionId=data.subscriptionId

     this.memberr.priceSUB=data.price
     this.memberr.subscriptionExpire=this.addDays(data.duration);
     this.memberr.subscriptionExpire.setHours(0,0,0,0);



   }
   
   )




} 


  addDays(days : number): Date{
    var futureDate = new Date();
    futureDate.setDate(futureDate.getDate() + days);
    futureDate.setHours(0,0,0,0);
    return futureDate;
  }


  getreceipt(m:Member){console.log(m)

    const logo='data:image/png;base64,'
 var doc = new jsPDF()
doc.setFontSize(35)
doc.text( 'Subscription Update',50, 30)

doc.setFontSize(20)
doc.text( 'Personal Informations--------------------------------------',20, 50)
doc.text( 'Name :',20, 60)
doc.text( 'Code :',20, 100)
doc.text('Email :',20, 70 )
doc.text('Phone :',20,  80)
doc.text('Adress :',20, 90 )
doc.text('Subscription :',20, 130 )
doc.text('Subscription Expire:',20, 140 )
doc.text('Payment method:',20, 150)
doc.text('Total  :',20, 160 )
doc.text( 'Payement Informations-----------------------------------',20, 120)

doc.setFont('helvetica')
doc.text( m.firstname+" "+m.lastname,50, 60)
doc.text(m.membercode,45, 100 )
doc.text(m.email,45, 70 )
doc.text(m.phone,48, 80 )
doc.text(m.adress,50, 90 )
doc.text(m.subscriptionName,70, 130)
doc.text(m.subscriptionExpire.toString().split('00')[0],90, 140)
doc.text(m.PayMethod,78, 150 )
doc.addImage(logo,'PNG', 180, 8, 30, 30)

doc.text(m.priceSUB+" MAD",45, 160 )


          
      doc.save('Receipt_'+m.membercode+'.pdf')



  }
}
